import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import CardGroup from "react-bootstrap/CardGroup";
import { Link, useLocation } from "react-router-dom";
import firebaseConfig from "../config";

const TestReceive = (props) => {
  // use hook
  const db = firebaseConfig.firestore().collection("User");
  const [name, setName] = useState();
  const [message, setMessage] = useState();
  const [fund, setFund] = useState();

  let Location = useLocation();

  console.log(Location);
  console.log(props, "props");

  let Projectname = Location.value.Projectname;

  console.log(Projectname, " Location.data.Id id here new");

  useEffect(() => {
    db.where("name", "==", `${Projectname}`).onSnapshot((querySnapshot) => {
      const projects = [];
      querySnapshot.forEach((doc) => {
        projects.push(doc.data());
      });
      putData();
    });
    // .catch(function (error) {
    //   console.log(error);
    // });
    // putData();
  }, []);

  function putData() {
    let getname = projects.name;
    let getmessage = projects.message;
    let getfund = projects.fund;

    setName(getname);
    setMessage(getmessage);
    setFund(getfund);
  }

  // const foo = () => console.log("arrow");

  // function bar() {
  //   console.log("standard");
  // }

  return (
    <>
      <Link to="/testsend">
        <Card.Link>
          <h1 className="mt-3">Back... </h1>
        </Card.Link>
      </Link>
      <CardGroup>
        <Card>
          <Card.Body>
            <Card.Title className="mt-5">
              id:{fund}. {name}
            </Card.Title>
            <Card.Text>{message}</Card.Text>
          </Card.Body>
        </Card>
      </CardGroup>
    </>
  );
};

export default TestReceive;
