let form = document.querySelector("#addUser");

form.addEventListener("submit", (e) => {
  e.preventDefault();
  db.collection("User").add({
    Name: form.Name.value,
    City: form.City.value,
  });

  //   reset input field
  form.Name.value = "";
  form.City.value = "";
});

export default form;
