import React, { userState, useEffect, useState } from "react";
import firebaseConfig from "../config";

function TestRealtimeFetching() {
  const [projects, setProject] = useState([]);
  const [type, setType] = useState("Science");
  const [status, setStatus] = useState(true);

  const db = firebaseConfig.firestore().collection("User");

  function getProject() {
    if (status) {
      db.onSnapshot((querySnapshot) => {
        const items = [];
        querySnapshot.forEach((doc) => {
          items.push(doc.data());
        });
        setProject(items);
      });
    }
  }

  function filterProject() {
    db.where("type", "==", type).onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setProject(items);
      setStatus(false);
    });
  }

  useEffect(() => {
    getProject();
  }, []);

  return (
    <div className="container">
      <div className="mt-5">
        <button
          className="btn btn-outline-primary"
          style={{ margin: `0px 15px 0px 0px` }}
          onClick={setType("Tech")}
        >
          Tech
        </button>
        <button
          className="btn btn-outline-primary"
          style={{ margin: `0px 15px 0px 0px` }}
        >
          Community
        </button>
        <button
          className="btn btn-outline-primary"
          style={{ margin: `0px 15px 0px 0px` }}
        >
          Science
        </button>
        <button
          className="btn btn-outline-danger"
          style={{ margin: `0px 15px 0px 0px` }}
        >
          Reset
        </button>
      </div>
      <div className="mt-5">
        <div className="col-sm-12">
          <div className="row row-cols-3">
            {projects.map((project) => (
              <div
                className="col-sm-4"
                key={project.fund}
                style={{ margin: `0px 0px 30px 0px` }}
              >
                <div className="card">
                  <div className="card-body">
                    <h4 className="card-title">{project.name}</h4>
                    <h6 className="card-subtitle mb-2 text-muted">
                      {project.type}
                    </h6>
                    <p className="card-text">{project.message}</p>
                    <p className="card-text">{project.fund}</p>
                    <a href={`mailto:${project.email}`} className="card-link">
                      {project.email}
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default TestRealtimeFetching;
