import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import CardGroup from "react-bootstrap/CardGroup";
import { Link, useLocation } from "react-router-dom";
import firebaseConfig from "../config";

const TestReceive = (props) => {
  // use hook
  const db = firebaseConfig.firestore().collection("User");
  const [name, setName] = useState();
  const [message, setMessage] = useState();
  // const [fund, setFund] = useState();

  let Location = useLocation();

  console.log(Location);
  console.log(props, "props");

  let Projectname = Location.value.Projectname;

  console.log(Projectname, " Location.data.Id id here new");

  var projects = [];

  useEffect(() => {
    db.where("name", "==", `${Projectname}`).onSnapshot((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        projects.push(doc.data());
      });
      // putData();
    });

    putData();
    console.log(projects(data.name));
  }, []);

  function putData() {
    let getname = projects.name;
    let getmessage = projects.message;
    // let getfund = projects.fund;

    setName(getname);
    setMessage(getmessage);
    // setFund(getfund);
  }

  return (
    <>
      <Link to="/testsend">
        <Card>
          <h1 className="mt-3">Back... </h1>
        </Card>
      </Link>
      <CardGroup>
        <Card>
          <Card.Body>
            <Card.Title className="mt-5">id:{name}</Card.Title>
            <Card.Text>{message}</Card.Text>
          </Card.Body>
        </Card>
      </CardGroup>
    </>
  );
};

export default TestReceive;
