import React, { useContext } from "react";
import { Redirect, Link } from "react-router-dom";
import { AuthContext } from "./Auth";
import firebaseConfig from "../config";
import Header from "./Header";

const LogIn = () => {
  const handleSubmit = (e) => {
    e.preventDefault();

    const { email, password } = e.target.elements;
    try {
      firebaseConfig
        .auth()
        .signInWithEmailAndPassword(email.value, password.value);
    } catch (error) {
      alert(error);
    }
  };

  const googleLogin = () => {
    // e.preventDefault();

    // const googleAuthProvider = new firebaseConfig.auth.GoogleAuthProvider();

    // firebaseConfig.auth().signInWithPopup(googleAuthProvider);
    //  catch (error) {
    //   alert(error);
    // }
    console.log("Test");
  };

  const { currentUser } = useContext(AuthContext);
  if (currentUser) {
    return <Redirect to="/" />;
  }

  return (
    <>
      <Header />
      <div className="container mt-5">
        <h1>Log in</h1>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="exampleInputEmail1" className="form-label">
              Email address
            </label>
            <input
              type="email"
              name="email"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
            <div id="emailHelp" className="form-text">
              We'll never share your email with anyone else.
            </div>
          </div>
          <div className="mb-3">
            <label htmlFor="exampleInputPassword1" className="form-label">
              Password
            </label>
            <input
              type="password"
              name="password"
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
          <Link to="signup" className="btn btn-success ms-2">
            Signup
          </Link>
        </form>
        <button onSubmit={googleLogin}>google</button>
      </div>
    </>
  );
};

export default LogIn;
