import React, { userState, useEffect, useState } from "react";
import firebaseConfig from "../config";

function TestRealtimeFetching() {
  const [projects, setProject] = useState([]);

  const db = firebaseConfig.firestore().collection("User");

  function getProject() {
    db.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setProject(items);
    });
  }

  useEffect(() => {
    getProject();
  }, []);

  return (
    <div>
      {/* <div className="container">
        {projects.map((project) => (
          <div className="container" key={project.id}>
            <h1>{project.name}</h1>
          </div>
        ))}
      </div> */}
      <div className="mt-5">
        <div className="col-sm-12">
          <div className="row row-cols-3">
            {projects.map((project) => (
              <div
                className="col-sm-4"
                key={project.fund}
                style={{ margin: `0px 0px 30px 0px` }}
              >
                <div className="card">
                  <div className="card-body">
                    <h4 className="card-title">{project.name}</h4>
                    <h6 className="card-subtitle mb-2 text-muted">
                      {project.type}
                    </h6>
                    <p className="card-text">{project.message}</p>
                    <p className="card-text">{project.fund}</p>
                    <a href={`mailto:${project.email}`} className="card-link">
                      {project.email}
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default TestRealtimeFetching;
